<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{:U('Flink/index')}" class="on">友情链接列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{:U('Flink/add')}','添加友情链接',520,250,true,false,false,addbtn,'add',true);">添加友情链接</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col> <col><col><col><col> <col width="140" align="center"> </colgroup>						<thead>							<tr>								<th>排序</th>								<th>编号</th>								<th>名称</th>								<th>描述</th>								<th>链接地址</th>								<th>编辑时间</th>								<th>状态</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($flink_list)">								<volist name="flink_list" id="vo">									<tr>										<td>{$vo.sort}</td>										<td>{$vo.id}</td>										<td>{$vo.name}</td>										<td>{$vo.info}</td>										<td>{$vo.url}</td>										<td>{$vo.add_time|date='Y-m-d H:i:s',###}</td>										<td><if condition="$vo['status']"><font color="green">显示</font><else/><font color="red">隐藏</font></if></td>										<td class="textcenter"><a href="javascript:void(0);" onclick="window.top.artiframe('{:U('Flink/edit',array('id'=>$vo['id'],'frame_show'=>true))}','查看详细信息',520,250,true,false,false,false,'add',true);">查看</a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{:U('Flink/edit',array('id'=>$vo['id']))}','编辑友情链接',520,250,true,false,false,editbtn,'add',true);">编辑</a> | <a href="javascript:void(0);" class="delete_row" parameter="id={$vo.id}" url="{:U('Flink/del')}">删除</a></td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="8">{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="8">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>